def numbers_10(array): #Функция numbers_10 принимает один аргумент - массив целых чисел
    result = [] #Создается пустой список result, в который будут добавляться четные числа, меньшие 10
    for num in array: #В цикле for происходит перебор каждого числа num из массива
        if num % 2 == 0 and num < 10: #если число num является четным и меньшим 10
            result.append(num) #оно добавляется в список result с помощью метода append()
    if not result: #После окончания цикла проверяется, были ли добавлены числа в список result
        print("В исходном массиве нет четных чисел меньших 10") #Если список result остался пустым выводится сообщение "В исходном массиве нет четных чисел меньших 10"
    else:
        result.sort() #Если список result содержит элементы, он сортируется с помощью метода sort()
        print(result) #выводится на экран
array = [1, 15, 6, 3, 8, 10, 4, 7]
numbers_10(array)
